mapper.select:
SELECT ic.NAME as name, ic.ID as id, iam_role.IS_ENABLED as role_is_enabled, iam_role.ID as role_id, iam_role.NAME as role_name, iam_role.CODE as role_code, iam_role.IS_BUILT_IN as role_is_built_in FROM (
SELECT * FROM ( SELECT TMP_PAGE.*, ROWNUM ROW_ID FROM (
SELECT * FROM oauth_client oc JOIN ( SELECT DISTINCT imr.member_id FROM oauth_client LEFT JOIN iam_member_role imr ON imr.member_id = oauth_client.id LEFT JOIN iam_role ir ON ir.id = imr.role_id WHERE imr.member_type = 'client' AND imr.source_id = #{sourceId} AND imr.source_type = #{sourceType}
AND imr.source_id = #{sourceId}
AND imr.source_type = #{sourceType}
AND oauth_client.NAME LIKE CONCAT('%', CONCAT(#{clientRoleSearchDTO.clientName}, '%'))
AND iam_role.NAME LIKE CONCAT('%', CONCAT(#{clientRoleSearchDTO.roleName}, '%'))
AND ( oauth_client.NAME LIKE concat(concat('%',#{param}),'%') OR oauth_client.ADDITIONAL_INFORMATION LIKE concat(concat('%',#{param}),'%') OR oauth_client.AUTHORIZED_GRANT_TYPES LIKE concat(concat('%',#{param}),'%') OR iam_role.name LIKE concat(concat('%',#{param}),'%') )
) t ON oc.ID = t.member_id
) TMP_PAGE
WHERE ROWNUM <= ((#{start} + 1) * #{size}) ) WHERE ROW_ID > (#{start} * #{size})
) ic LEFT JOIN iam_member_role ON iam_member_role.MEMBER_ID = ic.id LEFT JOIN iam_role ON iam_member_role.ROLE_ID = iam_role.ID LEFT JOIN iam_role_tl irt ON iam_role.id = irt.id WHERE iam_member_role.MEMBER_TYPE = 'client' AND irt.lang = #{lang} AND iam_member_role.source_id = #{sourceId} AND iam_member_role.source_type = #{sourceType} ORDER BY iam_member_role.LAST_UPDATE_DATE DESC

SELECT COUNT(1) FROM ( SELECT DISTINCT client.NAME as name, client.ID as id FROM oauth_client client INNER JOIN iam_member_role imr ON client.ID = imr.MEMBER_ID INNER JOIN iam_role role ON imr.ROLE_ID = role.ID WHERE imr.MEMBER_TYPE = 'client'
AND imr.source_id = #{sourceId}
AND imr.source_type = #{sourceType}
AND client.NAME LIKE CONCAT('%', CONCAT(#{clientRoleSearchDTO.clientName}, '%'))
AND role.NAME LIKE CONCAT('%', CONCAT(#{clientRoleSearchDTO.roleName}, '%'))
AND ( client.NAME LIKE concat(concat('%',#{param}),'%') OR client.ADDITIONAL_INFORMATION LIKE concat(concat('%',#{param}),'%') OR client.AUTHORIZED_GRANT_TYPES LIKE concat(concat('%',#{param}),'%') OR role.name LIKE concat(concat('%',#{param}),'%') )
) temp

mapper.delete:
DELETE
FROM iam_member_role
WHERE MEMBER_ID = #{memberId}
  AND MEMBER_TYPE = #{memberType}

